require("pixel")

 
function GLUP.draw_scene()
  radis=math.floor(GLUP.ElapsedTime()*4) % 4
  if radis==0 then
     draw_radis0(0,0)
  elseif radis==1 then
     draw_radis1(0,0)
  elseif radis==2 then
     draw_radis2(0,0)
  elseif radis==3 then
     draw_radis3(0,0)
  end
end

